import React, { useState } from 'react';
import { ArrowLeft, Search, ArrowRight } from 'lucide-react';

const regions = [
  'coimbatore',
  'chennai',
  'delhi',
  'lucknow',
  'madrid',
  'warangal',
  'bangalore',
  'pallakad',
  'coimbatore'
];

export default function SelectRegionPage() {
  const [search, setSearch] = useState('');
  const [selected, setSelected] = useState('coimbatore');

  return (
    <div className="min-h-screen bg-black text-white px-4 py-6 flex flex-col justify-between">
      {/* Top Section */}
      <div>
        <button className="mb-6">
          <ArrowLeft className="text-white" />
        </button>

        <h1 className="text-2xl font-bold mb-2">Select your Region</h1>
        <p className="text-sm text-gray-400 mb-4">
          Lorem ipsum dolor sit amet consectetur. Sit porta blandit montes cursus. Tempus accumsan
          mauris in cras sit. <span className="underline">Learn more</span>
        </p>

        {/* Search Box */}
        <div className="flex items-center bg-zinc-900 rounded-lg px-3 py-2 border border-zinc-700 mb-4">
          <Search className="text-zinc-500 mr-2" size={18} />
          <input
            type="text"
            className="bg-transparent outline-none w-full text-sm"
            placeholder="search for region"
            value={search}
            onChange={(e) => setSearch(e.target.value)}
          />
        </div>

        {/* Selected Region */}
        <div className="mb-4">
          <button className="border border-pink-400 text-pink-400 px-4 py-1 rounded-full text-sm">
            {selected}
          </button>
        </div>

        {/* College Region List */}
        <p className="font-semibold text-white mb-2">Youre college is in</p>
        <div className="flex flex-wrap gap-2">
          {regions.map((region, index) => (
            <button
              key={index}
              onClick={() => setSelected(region)}
              className="border border-zinc-600 px-4 py-1 rounded-full text-sm capitalize hover:border-pink-400 hover:text-pink-400 transition"
            >
              {region}
            </button>
          ))}
        </div>
      </div>

      {/* Bottom Section */}
      <div className="flex items-center justify-between mt-10">
        <p className="text-sm text-white">
          Canâ€™t find your Region ?{' '}
          <span className="text-pink-400 underline cursor-pointer">Add em</span>
        </p>
        <button className="bg-zinc-800 w-12 h-12 rounded-full flex items-center justify-center">
          <ArrowRight className="text-white" />
        </button>
      </div>
    </div>
  );
}
