import React, { useState } from 'react';
import { ArrowLeft, ArrowRight, Pencil } from 'lucide-react';

const prompts = [
  'I’m known for',
  'My simple please are',
  'I’m happiest when',
  'I’m wanna be a',
  'Stolen',
];

export default function PromptEditor() {
  const [activePromptIndex, setActivePromptIndex] = useState(null);
  const [responses, setResponses] = useState({});
  const [inputValues, setInputValues] = useState({});

  const handleTogglePrompt = (index) => {
    setActivePromptIndex(index === activePromptIndex ? null : index);
  };

  const handleInputChange = (index, value) => {
    setInputValues({ ...inputValues, [index]: value });
  };

  const handleSave = (index) => {
    setResponses({ ...responses, [index]: inputValues[index] || '' });
    setActivePromptIndex(null);
  };

  return (
    <div className="min-h-screen bg-black text-white px-4 py-6 flex flex-col justify-between">
      {/* Header */}
      <div>
        <button className="mb-6">
          <ArrowLeft className="text-white" size={24} />
        </button>

        <h1 className="text-2xl font-bold">give a<br />try with prompts</h1>
        <p className="text-sm text-zinc-400 mt-2 mb-5">
          Lorem ipsum dolor sit amet consectetur. Pulvinar risus donec aenean
          tristique risus eu vitae felis. Donec lacus accumsan ultricies metus.
        </p>

        {/* Prompt List */}
        <div>
          {prompts.map((prompt, index) => (
            <div key={index} className="border-b border-zinc-800 py-3">
              <div
                className="flex justify-between items-center"
                onClick={() => handleTogglePrompt(index)}
              >
                <span className="text-base">
                  {prompt}
                  {responses[index] && (
                    <span className="ml-2 text-sm text-zinc-400">
                      - {responses[index]}
                    </span>
                  )}
                </span>
                <Pencil size={18} className="text-zinc-400" />
              </div>

              {activePromptIndex === index && (
                <div className="mt-4">
                  <textarea
                    rows={4}
                    maxLength={150}
                    placeholder="Open up now"
                    value={inputValues[index] || ''}
                    onChange={(e) => handleInputChange(index, e.target.value)}
                    className="w-full border border-zinc-600 rounded-md p-2 bg-transparent text-white placeholder-zinc-500 text-sm outline-none"
                  />
                  <div className="text-xs text-zinc-500 mt-1">
                    {inputValues[index]?.length || 0}/150
                  </div>
                  <button
                    onClick={() => handleSave(index)}
                    className="text-[#F06CB7] text-sm mt-2"
                  >
                    Save & Close
                  </button>
                </div>
              )}
            </div>
          ))}
        </div>
      </div>

      {/* Footer */}
      <div className="flex justify-end mt-6">
        <button className="w-12 h-12 rounded-full bg-[#2e2e2e] flex items-center justify-center">
          <ArrowRight className="text-white" size={22} />
        </button>
      </div>
    </div>
  );
}
